import '../App.css'
import '../bootstrap.min.css'
import Planet from "../Components/Planet";
import Timer from "../Components/Timer";
import Album from "../Components/album";
import Text2 from "./Text2";
import Earth from "./Earth";
import Uranus from "./Uranus";
import {useState, useEffect} from "react";

function Home(props) {

    const [currentScreen, setCurrentScreen] = useState(<Earth/>);
    const [estilo, setEstilo] = useState("")

    useEffect(() => {
        console.log("inside useeefect", props.wheel)
        console.log("inside 2", props.motion)

    //inicio da animação da Terra
        if (props.wheel === 5 && props.motion === 1) {
            //scroll up
            setEstilo("fade-in2")
            setCurrentScreen(<Earth/>)
        }
        if (props.wheel === 10 && props.motion === 0) {
            setEstilo("fade-out1")
        }
        if (props.wheel === 20 && props.motion === 1) {
            setEstilo("fade-in1 centra")
            setCurrentScreen(<Earth/>)
        }
        if (props.wheel === 25 && props.motion === 0) {
            setEstilo("fade-out2 centra")
        }
    //fim da animação da Terra
        if (props.wheel === 30 && props.motion === 0) {
            setCurrentScreen(<Text2/>)
        }

    //inicio da animação de Uranus
        if (props.wheel === 37 && props.motion === 1) {
            setEstilo("fade-out2 centra")
        }
        if (props.wheel === 40 && props.motion === 0) {
            setCurrentScreen(<Uranus/>)
        }
        if (props.wheel === 45 && props.motion === 0) {
            setEstilo("fade-in1 centra")
        }
        if (props.wheel === 54 && props.motion === 1) {
            setEstilo("fade-out1 centra")

        }

        if (props.wheel === 55 && props.motion === 0) {

            setEstilo("fade-in2 centra")
        }
        //fim da animação da Uranus


        if (props.wheel === 200) {
            setCurrentScreen(<Uranus/>)
        }
    }, [props.wheel]);

    return (

        <div className={"bgblack container-fluid"}>
            <div className={"sticky-top"}>
                <div className={"timer"}>
                    <span className={"text-white amiri"}><Timer></Timer></span>
                </div>
                <div className={"counter"}>
                    <div className={"frac text-white amiri"}>
                        <span>1</span>
                        <span className={"symbol"}>/</span>
                        <span className={"bottom"}>2</span>
                    </div>
                </div>
                <div className={"days"}>
                    <span className={"text-white amiri"}>365 days</span>
                </div>
                <div className={"planeti"}>
                    <span className={"text-white amiri"}> Earth </span>
                </div>
            </div>
            <div className={"bggrain"}>
                <div className={estilo}>
                {currentScreen}
                </div>
            </div>


        </div>
    );
}

export default Home;
