import '../App.css'
import '../bootstrap.min.css'
import Planet from "../Components/Planet";
import Timer from "../Components/Timer";
import Album from "../Components/album";
import {useState, useEffect} from "react";

function Detail() {

    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // Make API call to WordPress site to retrieve posts
        fetch('http://spaceuc.local/wp-json/wp/v2/posts')
            .then(res => res.json())
            .then(data => setPosts(data));
    }, []);



    return (

        <div  className={"bgblack container-fluid"}>
            <div className={"sticky-top"}>
                <div className={"timer"}>
                    <span className={"text-white amiri"}><Timer></Timer></span>
                </div>
                <div className={"counter"}>
                    <div className={"frac text-white amiri"}>

                        <span className={"symbol"}>/</span>
                        <span className={"bottom"}>2</span>
                    </div>
                </div>
                <div className={"days"}>
                    <span className={"text-white amiri"}> days</span>
                </div>
                <div className={"planeti"}>
                    <span className={"text-white amiri"}>  </span>
                </div>
            </div>
            <div className={"bggrain text-white"}>
                <div >
                    <aside>
                        <img className={"imgdetail"} src={"./Earth/dark.jpg"} />
                    </aside>
                    <div>
                        {posts[2] &&
                            <div key={posts[2].id}>
                                <h2 className={"amiri"}>{posts[1].title.rendered}</h2>
                                <div dangerouslySetInnerHTML={{ __html: posts[1].content.rendered }} />
                            </div>
                        }
                    </div>
                </div>
            </div>


        </div>
    );
}

export default Detail;
